} let input = "回復以外から火ドロップを6個生成。";
Skill { sub_effects: None, turns_of_apply: None, effect: SkillEffect::GenRandomDrop( vec![ Drop::NonColored(NonColoredDrop::Recovery), Drop::Colored(Color::Fire) ], vec![(Drop::Colored(Color::Fire), 6)], ),
} let input = "ランダムで木ドロップを11個、闇ドロップを7個、回復ドロップを5個生成。";
